using System;
using System.ComponentModel.DataAnnotations;
using System.ComponentModel.DataAnnotations.Schema;
using System.IO;

namespace Teste.Domain.Entities
{
    public class Photo : BaseEntitie
    {
        private const string SemFoto = "data:image/png;base64,";
        private const string SemFotoThumb = "data:image/png;base64,";

        public string Caminho { get; set; }

        [NotMapped]
        public string GetImgBase64 { get; set; }

        [System.ComponentModel.DefaultValue("")]
        public string AspNetUsersId { get; set; }

        [NotMapped]
        public virtual string Imagem
        {
            get
            {
                if (!string.IsNullOrEmpty(Caminho) && File.Exists(Caminho))
                {
                    return Convert.ToBase64String(File.ReadAllBytes(Caminho));
                }
                // Se ainda não foi gravada uma imagem efetivamente,
                // ou se a imagem gravada foi eliminada, vamos retornar uma imagem padrão.
                return SemFoto;
            }
        }

        [NotMapped]
        public virtual string Thumbnail
        {
            get
            {
                if (!string.IsNullOrEmpty(Caminho) && File.Exists(Caminho))
                {
                    return Convert.ToBase64String(File.ReadAllBytes(Caminho).MakeThumbnail(80, 80));
                }
                // Se ainda não foi gravada uma imagem efetivamente,
                // ou se a imagem gravada foi eliminada, vamos retornar uma imagem padrão.
                return SemFotoThumb;
            }
        }
    }
}